               let userId = {{auth()->user()->id}};
               $.ajax({
                url:'shop/status/update',
                method:'post',
                data: {
                    _token: $('meta[name="csrf-token"]').attr('content'),
                    user_id:userId,
                    status:status,
                },
                success: function(response){
                        if(response.message == true){
                            AIZ.plugins.notify('success', '{{ translate('Shop status has been updated') }}');
                            isSubmitting = true;
                        }
                },
                error: function(){

                }

               });
            });
        });
